    private void backspaceBTActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_backspaceBTActionPerformed
        int length = data.getText().length();
        int number = data.getText().length();
        String save;
        if (length > 0) {
            StringBuilder back = new StringBuilder(data.getText());
            back.deleteCharAt(number);
            save = back.toString();
            data.setText(save);
        }
    }//GEN-LAST:event_backspaceBTActionPerformed

