    double number1;
    int kalkulator;

    private void tampilkanHasil(){
        double number2 = Double.parseDouble(data.getText());
        double hasil = 0;

        switch(kalkulator) {
            case 1:
                hasil = number1 + number2;
                break;
            case 2:
                hasil = number1 - number2;
                break; 
            case 3:
                hasil = number1 * number2;
                break;
            case 4:
                if(number2 != 0) {
                    hasil = number1 / number2;
                } else {
                    data.setText("Tidak bisa di bagi 0");
                }
                break;
            case 5:
                hasil = (number1 * number2) / 100;
                break;
        }
        data.setText(Double.toString(hasil));
    }
